$(document).ready(()=>{const classBody=$("body"),btnSwitch=$("#switch");$(btnSwitch).on("click",()=>{classBody.toggleClass("dark"),btnSwitch.toggleClass("active"),classBody.hasClass("dark")?localStorage.setItem("dark-mode","true"):localStorage.setItem("dark-mode","false")}),"true"==localStorage.getItem("dark-mode")?(classBody.addClass("dark"),btnSwitch.toggleClass("active")):(classBody.removeClass("dark"),btnSwitch.removeClass("disable"))});